var Message = require('../message');

FlexMessage.prototype = new Message();
FlexMessage.prototype.constructor = FlexMessage;

function FlexMessage() {
  var that = this;
  var texts = arguments;
  var message = [];

  this.buildMessage = function() {
    if ( message.length ) return message;

    for ( var i in texts )
      message.push({
        type : "flex",
        altText: "Report",
        contents:JSON.parse(texts[i])
      });
    console.log(message[0]);
    return message;
  }
}

module.exports = FlexMessage;
